import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { t, getLang, onLangChange } from '../../translation';

const AssetsCardSection = ({ data = [], isLoading }) => {
  const [lang, setLangState] = useState(getLang());
  useEffect(() => {
    const off = onLangChange(() => setLangState(getLang()));
    return off;
  }, []);

  if (isLoading) {
    return <div className="text-center py-6">{t('page.mypage.common.loading')}</div>;
  }

  if (data.length === 0) {
    return (
      <div className="w-full mt-4">
        <p className="text-center py-6">{t('page.mypage.common.noData')}</p>
      </div>
    );
  }

  const formatDate = (dateString) => {
    if (!dateString) return '2025.06.12';
    // 이미 포맷된 문자열인 경우 그대로 반환
    if (typeof dateString === 'string' && dateString.match(/^\d{4}\.\d{2}\.\d{2}$/)) {
      return dateString;
    }
    // Date 객체나 ISO 문자열인 경우 포맷
    try {
      const date = new Date(dateString);
      if (isNaN(date.getTime())) return '2025.06.12';
      const year = date.getFullYear();
      const month = String(date.getMonth() + 1).padStart(2, '0');
      const day = String(date.getDate()).padStart(2, '0');
      return `${year}.${month}.${day}`;
    } catch {
      return '2025.06.12';
    }
  };

  return (
    <div className="w-full mt-4 space-y-4">
      {data.map((item, index) => (
        <div
          key={item.marketNo || index}
          className="bg-white border border-gray-200 rounded-lg p-6 hover:shadow-md transition-shadow relative"
        >
          {/* 상단: 태그 및 보관함담기 버튼 */}
          <div className="flex justify-between items-start mb-4">
            <div className="flex flex-wrap gap-2">
              <span className="px-2 py-1 text-xs border border-gray-300 rounded bg-white text-gray-700">
                공공데이터포털
              </span>
              <span className="px-2 py-1 text-xs rounded bg-blue-600 text-white">
                {item.category || '건설에너지'}
              </span>
              <span className="px-2 py-1 text-xs border border-gray-300 rounded bg-white text-gray-700">
                {item.price ? '유료' : '무료'}
              </span>
            </div>
            <button className="flex items-center text-gray-600 hover:text-gray-800 text-sm">
              <svg 
                className="w-5 h-5 mr-1" 
                fill="none" 
                stroke="currentColor" 
                viewBox="0 0 24 24"
              >
                <path 
                  strokeLinecap="round" 
                  strokeLinejoin="round" 
                  strokeWidth={1.5} 
                  d="M5 5a2 2 0 012-2h10a2 2 0 012 2v16l-7-3.5L5 21V5z" 
                />
              </svg>
              보관함담기
            </button>
          </div>

          {/* 제목 */}
          <h3 className="text-xl font-bold text-gray-900 mb-3">
            <Link 
              to={`/mypage/market/selldetail/${item.marketNo}`}
              className="hover:text-blue-600 transition-colors"
            >
              {item.marketAssetName}
            </Link>
          </h3>

          {/* 설명 */}
          <p className="text-gray-700 text-sm leading-relaxed mb-4">
            {item.description || `${item.marketAssetName}에 대한 상세 정보입니다. 이 데이터에는 다양한 정보가 포함되어 있습니다.`}
          </p>

          {/* 하단: 등록일 */}
          <div className="flex justify-between items-center">
            <span className="text-sm text-gray-600">
              등록일 {formatDate(item.registrationDate || item.regDate)}
            </span>
          </div>
        </div>
      ))}
    </div>
  );
};

export default AssetsCardSection;
