import React, { useState, useEffect, useRef } from 'react';
import LeftMenu from '../common/LeftMenu';
import MarketSearchSection from './MarketSearchSection';
import CardSection from './MarketCardSection';
import useAxiosApi from '../../hooks/useAxiosApi';
import { t, getLang, onLangChange } from '../../translation';

const MarketContainer = () => {
  const [assetData, setAssetData] = useState([]);
  const [searchCondition, setSearchCondition] = useState({
    startDttm: '',
    endDttm: '',
    word: '',
  });
  const [pageNo, setPageNo] = useState(1);
  const [hasMore, setHasMore] = useState(true);
  const [isLoading, setIsLoading] = useState(false);
  const isFetchingRef = useRef(false);
  const prevSearchRef = useRef(JSON.stringify(searchCondition));
  const [lang, setLangState] = useState(getLang());

  const params = new URLSearchParams();
  Object.keys(searchCondition).forEach(key => {
    if (searchCondition[key]) {
      params.append(key, searchCondition[key]);
    }
  });
  params.append('pageNo', pageNo);
  const queryString = params.toString();
  const endpoint = `/market${queryString ? `?${queryString}` : ''}`;
  const { execute: fetchMarketData } = useAxiosApi(endpoint, 'GET');

  useEffect(() => {
    const off = onLangChange(() => setLangState(getLang()));
    return off;
  }, []);

  useEffect(() => {
    let ignore = false;

    const fetchData = async () => {
      if (isFetchingRef.current) {
        return;
      }

      isFetchingRef.current = true;
      setIsLoading(true);

      try {
        const newData = await fetchMarketData();
        if (!ignore && newData && newData.data) {
          const { list, totalPage } = newData.data;
          const currentSearch = JSON.stringify(searchCondition);
          const isNewSearch = prevSearchRef.current !== currentSearch;

          if (isNewSearch) {
            setAssetData(list);
            prevSearchRef.current = currentSearch;
          } else {
            setAssetData(prev => [...prev, ...list]);
          }

          setHasMore(pageNo < totalPage);
        }
      } catch (err) {
        if (!ignore) {
          setHasMore(false);
          console.error('Error fetching market data:', err);
        }
      } finally {
        if (!ignore) {
          setIsLoading(false);
          isFetchingRef.current = false;
        }
      }
    };

    fetchData();

    return () => {
      ignore = true;
    };
  }, [searchCondition, pageNo]);

  const handleSearchChange = condition => {
    setAssetData([]);
    setHasMore(true);
    setPageNo(1);
    setSearchCondition(condition);
  };

  const loadMoreData = () => {
    if (hasMore && !isLoading && !isFetchingRef.current) {
      setPageNo(prev => prev + 1);
    }
  };

  return (
    <>
      <main>
        <section className="flex justify-center mt-10 mb-12">
          <LeftMenu pageName="list" />
          <section className="flex-1 ml-4 max-w-screen-lg">
            <div className="flex justify-between">
              <h2 className="text-2xl mb-5">{t('page.market.title')}</h2>
            </div>
            <MarketSearchSection onSearch={handleSearchChange} />
            <CardSection
              assetData={assetData}
              isLoading={isLoading}
              loadMoreData={loadMoreData}
              hasMore={hasMore}
            />
          </section>
        </section>
      </main>
    </>
  );
};

export default MarketContainer;
