const MarketSellAssetsContainer = () => {
  const [searchCondition, setSearchCondition] = useState({
    startDttm: '',
    endDttm: '',
    state: '',
    word: '',
  });

  const [currentSellPage, setCurrentSellPage] = useState(1);
  const [sellData, setSellData] = useState(null);
  const [isSellingLoading, setIsSellingLoading] = useState(false);
  const ITEMS_PER_PAGE = 4; // 페이지당 아이템 수

  const buildQueryParams = () => {
    const params = new URLSearchParams();
    Object.keys(searchCondition).forEach(key => {
      if (searchCondition[key]) {
        params.append(key, searchCondition[key]);
      }
    });
    params.append('pageNo', currentSellPage);
    return params.toString();
  };

  const queryParams = buildQueryParams();
  const endpoint = `/market/mypage${queryParams ? `?${queryParams}` : ''}`;
  const { execute: fetchMyAssets } = useAxiosApi(endpoint, 'GET');

  useEffect(() => {
    const fetchData = async () => {
      try {
        setIsSellingLoading(true);
        // 가데이터 사용 (개발용)
        const USE_MOCK_DATA = true; // false로 변경하면 실제 API 호출

