          // 검색 조건에 따라 필터링된 가데이터 반환
          await new Promise(resolve => setTimeout(resolve, 500)); // 로딩 시뮬레이션

          let filteredList = [...MOCK_SELL_DATA.data.list];

          // 검색어 필터링
          if (searchCondition.word) {
            filteredList = filteredList.filter(item =>
              item.marketAssetName.toLowerCase().includes(searchCondition.word.toLowerCase())
            );
          }

          // 상태 필터링
          if (searchCondition.state) {
            filteredList = filteredList.filter(item => item.state === searchCondition.state);
          }

          // 전체 페이지 수 계산
          const totalItems = filteredList.length;
          const totalPages = Math.ceil(totalItems / ITEMS_PER_PAGE);

          // 현재 페이지에 해당하는 데이터만 추출
          const startIndex = (currentSellPage - 1) * ITEMS_PER_PAGE;
          const endIndex = startIndex + ITEMS_PER_PAGE;
          const paginatedList = filteredList.slice(startIndex, endIndex);

          setSellData({
            data: {
              list: paginatedList,
              totalPage: totalPages > 0 ? totalPages : 1,
            },
          });
        } else {
          // 실제 API 호출
          const data = await fetchMyAssets();
          setSellData(data);
        }
      } catch (err) {
        console.error('판매 에셋 데이터 가져오기 에러:', err.message);
        // 에러 발생 시에도 가데이터 표시
        setSellData(MOCK_SELL_DATA);
      } finally {
        setIsSellingLoading(false);
      }
    };

    fetchData();
  }, [searchCondition, currentSellPage]);

  const handleSearchChange = condition => {
    setSearchCondition(condition);
    setCurrentSellPage(1);
  };

  const [lang, setLangState] = useState(getLang());
  useEffect(() => {
    const off = onLangChange(() => setLangState(getLang()));
    return off;
  }, []);

  return (
    <>
      <main>
        <section className="flex justify-center mt-16">
          <LeftMenu pageName="mypage" />
          <section className="flex-1 ml-4 max-w-screen-lg">
            <div className="flex justify-between">
              <h2 className="text-2xl mb-5">{t('page.mypage.marketSell.title')}</h2>
            </div>

            <SearchSection onSearch={handleSearchChange} />

            <AssetsCardSection
              data={sellData?.data?.list || []}
              isLoading={isSellingLoading}
            />
            <Pagination
              currentPage={currentSellPage}
              totalPage={sellData?.data?.totalPage || 0}
              onPageChange={setCurrentSellPage}
            />
          </section>
        </section>
      </main>
    </>
  );
};

export default MarketSellAssetsContainer;
