import React, { useEffect, useState } from 'react';
import { getLang, onLangChange } from '../../translation';

const MarketCardSection = () => {
  const [lang, setLangState] = useState(getLang());

  useEffect(() => {
    const off = onLangChange(() => setLangState(getLang()));
    return off;
  }, []);

  // Static data replicating the layout in the image
  const cards = [
    {
      category: "건설에너지",
      title: "스마트불편신고 상위 지역",
      description: "용도지역(주거지역, 상업지역)별 스마트불편신고 (불법주정차, 불법광고물, 쓰레기, 장애인주차구...",
      linkText: "공간빅데이터분석"
    },
    {
        category: "건설에너지",
        title: "스마트불편신고 상위 지역",
        description: "용도지역(주거지역, 상업지역)별 스마트불편신고 (불법주정차, 불법광고물, 쓰레기, 장애인주차구...",
        linkText: "공간빅데이터분석"
    },
    {
        category: "건설에너지",
        title: "스마트불편신고 상위 지역",
        description: "용도지역(주거지역, 상업지역)별 스마트불편신고 (불법주정차, 불법광고물, 쓰레기, 장애인주차구...",
        linkText: "공간빅데이터분석"
    }
  ];

  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 mb-16">
      {cards.map((card, index) => (
        <div
          key={index}
          className="bg-[#edf6ff] p-6 rounded-2xl shadow-sm hover:shadow-md transition-shadow duration-300 flex flex-col justify-between h-64"
        >
          <div>
            <div className="text-blue-600 font-semibold mb-2 text-sm">
              {card.category}
            </div>
            <h3 className="text-lg font-bold text-gray-900 mb-4 leading-snug">
              {card.title}
            </h3>
            <p className="text-gray-600 text-sm leading-relaxed break-keep">
              {card.description}
            </p>
          </div>

          <div className="mt-4">
            <button className="bg-white text-gray-800 text-sm font-medium py-2 px-4 rounded-full border border-gray-200 flex items-center shadow-sm hover:bg-gray-50 transition-colors">
              {card.linkText}
              <svg 
                className="w-4 h-4 ml-2 text-gray-400" 
                fill="none" 
                stroke="currentColor" 
                viewBox="0 0 24 24"
              >
                <path 
                  strokeLinecap="round" 
                  strokeLinejoin="round" 
                  strokeWidth={2} 
                  d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14" 
                />
              </svg>
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default MarketCardSection;
