import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import LeftMenu from '../common/LeftMenu';
import Swal from 'sweetalert2';
import AssetChainSections from '../common/AssetChainSections';
import useAxiosApi from '../../hooks/useAxiosApi';

const MarketSellDetailContainer = () => {
  const { marketNo } = useParams();
  const navigate = useNavigate();
  const [isEditMode, setIsEditMode] = useState(false);
  const [detailData, setDetailData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    productType: '데이터서비스',
    language: '한국어',
    keywords: '',
    landingUrl: '',
    topic: '',
    publisher: '',
    price: '',
    stock: '',
    saleStartDate: '',
    saleEndDate: '',
    state: '2',
    stateDesc: '판매중',
  });

  const { execute: fetchMarketDetail } = useAxiosApi(`/market/${marketNo}`, 'GET');

  useEffect(() => {
    const fetchData = async () => {
      try {
        setIsLoading(true);
        const response = await fetchMarketDetail();

        // 서버 응답 구조: { resultCode, resultMessage, data }
        if (response && response.resultCode === 200 && response.data) {
          console.log('Market detail response:', response);
          const data = response.data;
          setDetailData(data);

          // 날짜 포맷팅 (YYYY-MM-DD HH:mm:ss -> YYYY-MM-DD)
          const formatDateForInput = (dateString) => {
            if (!dateString) return '';
            return dateString.split(' ')[0];
          };

          // 상세보기 모드일 때는 데이터만 표시
          setFormData({
            title: data.marketDataName || data.marketDatatName || '',
            description: data.marketDataDesc || '',
            productType: data.marketProductType || '데이터서비스',
            language: data.marketLanguage || '한국어',
            keywords: data.marketKeyword || '',
            landingUrl: data.marketDoiUrl || '',
            topic: data.marketSubject || '',
            publisher: data.marketIssuer || '',
            price: data.price || 0,
            stock: data.inventoryCnt || 0,
            saleStartDate: formatDateForInput(data.startDttm) || '',
            saleEndDate: formatDateForInput(data.endDttm) || '',
            state: data.state || '2',
            stateDesc: data.stateDesc || '판매중',
          });
        } else {
          console.error('Invalid response structure:', response);
          Swal.fire('오류', '데이터를 불러올 수 없습니다.', 'error');
        }
      } catch (err) {
        console.error('Error fetching market detail:', err);
        Swal.fire('오류', '데이터를 불러오는 중 오류가 발생했습니다.', 'error');
      } finally {
        setIsLoading(false);
      }
    };

    if (marketNo) {
      fetchData();
    }
  }, [marketNo, fetchMarketDetail]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    Swal.fire({
      title: '수정 완료',
      text: '판매 데이터가 성공적으로 수정되었습니다.',
      icon: 'success',
      confirmButtonText: '확인'
    }).then(() => {
      setIsEditMode(false);
    });
  };

  const handleEdit = () => {
    setIsEditMode(true);
  };

  const handleCancel = () => {
    setIsEditMode(false);
    // 원래 데이터로 복원
    setFormData({
      title: detailData.marketAssetName || '',
      description: detailData.description || '',
      productType: detailData.productType || '데이터서비스',
      language: detailData.language || '한국어',
      keywords: detailData.keywords || '',
      landingUrl: detailData.landingUrl || '',
      topic: detailData.topic || '',
      publisher: detailData.publisher || '',
      price: detailData.price || 0,
      stock: detailData.stock || 0,
      saleStartDate: detailData.saleStartDate || '',
      saleEndDate: detailData.saleEndDate || '',
      state: detailData.state || '2',
      stateDesc: detailData.stateDesc || '판매중',
    });
  };

  const getStatusBadgeColor = (stateDesc) => {
    if (stateDesc.includes('판매중') || stateDesc.includes('제공중')) {
      return 'bg-green-100 text-green-800 border-green-300';
    } else if (stateDesc.includes('판매전') || stateDesc.includes('제공전')) {
      return 'bg-gray-100 text-gray-800 border-gray-300';
    } else if (stateDesc.includes('판매완료')) {
      return 'bg-blue-100 text-blue-800 border-blue-300';
    } else if (stateDesc.includes('판매종료') || stateDesc.includes('제공종료')) {
      return 'bg-red-100 text-red-800 border-red-300';
    }
    return 'bg-gray-100 text-gray-800 border-gray-300';
  };

  const getStatusOptions = () => {
    return [
      { value: '1', label: '판매전' },
      { value: '2', label: '판매중' },
      { value: '3', label: '판매중지' },
      { value: '4', label: '판매종료' },
