          issuanceDate: vcData.registrationDate || new Date().toISOString(),
          expirationDate: vcData.registrationDate ? new Date(new Date(vcData.registrationDate).getTime() + 365 * 24 * 60 * 60 * 1000).toISOString() : new Date().toISOString(),
          credentialSubject: {
