import React, { useEffect, useState } from 'react';
import AssetCardSectionSkeleton from '../../skeletonui/assets/AssetCardSectionSkeleton';
import InfiniteScroll from 'react-infinite-scroll-component';
import { t, getLang, onLangChange } from '../../translation';

const MarketCardSection = ({ assetData, isLoading, loadMoreData, hasMore }) => {
  const [lang, setLangState] = useState(getLang());
  useEffect(() => {
    const off = onLangChange(() => setLangState(getLang()));
    return off;
  }, []);
  const handleLoadMore = () => {
    if (!isLoading && hasMore) {
      loadMoreData();
    }
  };
  return (
    <InfiniteScroll
      dataLength={assetData.length}
      next={handleLoadMore}
      hasMore={hasMore}
      loader={<AssetCardSectionSkeleton />}
    >
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 mb-16">
        {assetData.map((item, index) => {
          const mv = String(item.metaverseName || '').toLowerCase().trim();
          const isRoblox = mv === '로블록스' || mv === 'roblox';
          const isZepeto = mv === '제페토' || mv === 'zepeto';
          const isIncheon =
            mv === '인천월드' || mv === 'incheon world' || mv === 'incheonworld' || mv === 'incheon-world';
          const label = isRoblox ? 'roblox' : isZepeto ? 'zepeto' : isIncheon ? 'inc-world' : 'rpm';

          const labelColor = isRoblox
            ? 'bg-green-200 text-green-800'
            : isZepeto
              ? 'bg-yellow-200 text-yellow-800'
              : isIncheon
                ? 'bg-purple-200 text-purple-800'
                : 'bg-blue-200 text-blue-800';

          return (
            <div
              key={index}
              className="bg-white p-6 rounded-lg shadow-lg transform transition-transform duration-300 hover:scale-105 cursor-pointer"
            >
              <a
                href={`/market/detail/${item.marketNo}`}
                className="block relative"
              >
                <img
                  src={item.fileUrlFirst}
                  alt="market image"
                  className="w-full h-48 object-contain bg-[#e4e6ea] rounded-t-lg"
                />
                <span
                  className={`absolute top-2 right-2 px-2 py-1 text-xs font-semibold rounded-full ${labelColor}`}
                >
                  {label}
                </span>
              </a>
              <div className="mt-4">
                <h3 className="text-xl font-semibold mb-2">
                  {item.marketAssetName || 'Default Asset Name'}
                </h3>
                <p className="text-gray-600">{t('page.market.list.card.creator')}: {item.saleUserName}</p>
                <p className="text-gray-600">
                  {t('page.market.list.card.metaverse') + ': ' + item.metaverseName}
                </p>
                <p className="text-gray-700">
                  {t('page.market.list.card.stock', { stock: item?.inventoryCnt ?? 0, total: item?.issueCnt ?? 0 })}
                </p>
                <p className="text-blue-500 font-semibold">
                  {t('page.market.list.card.price', { price: item.price })}
                </p>
              </div>
            </div>
          );
        })}
      </div>
    </InfiniteScroll>
  );
};

export default MarketCardSection;
