import React, { useState, useEffect } from 'react';
import AssetInfoSectionSkeleton from '../../skeletonui/assetsdetail/AssetInfoSectionSkeleton';
import { formatDate } from '../../utils/formatDate';
import timeAgoFromNow from '../../utils/timeAgoFromNow';
import useAxiosApi from '../../hooks/useAxiosApi';
import Swal from 'sweetalert2';
import { t, getLang, onLangChange } from '../../translation';

const AssetInfoSection = ({ assetData }) => {
  const [isPurchaseInProgress, setIsPurchaseInProgress] = useState(false);
  const { execute: purchaseAsset } = useAxiosApi('/purchase', 'POST');
  const [lang, setLangState] = useState(getLang());
  useEffect(() => {
    const off = onLangChange(() => setLangState(getLang()));
    return off;
  }, []);

  const handlePurchase = async () => {
    if (isPurchaseInProgress || !assetData) return;

    const maxQty = assetData.issueCnt - assetData.saleCnt;
    const { value: quantity } = await Swal.fire({
      title: t('page.marketdetail.purchase.confirmTitle'),
      html: `
    <div style="display: flex; justify-content: center; align-items: center; flex-direction: column; text-align: center;">
      <p>${t('page.marketdetail.purchase.currentStock', { stock: maxQty })}</p>
      <div style="display: flex; justify-content: center; align-items: center; margin-top: 20px;">
        <span style="margin-right: 10px; font-size: 16px; font-weight: bold;">${t('page.marketdetail.purchase.qtyLabel')}</span>
        <input type="number" id="purchaseQty" value="1" min="1" max="${maxQty}" 
          placeholder="${t('page.marketdetail.purchase.qtyPlaceholder')}" 
          style="width: 50px; padding: 8px; font-size: 16px; 
                 border: 2px solid #87CEEB; border-radius: 8px; 
                 text-align: center; box-shadow: 0 4px 6px rgba(135, 206, 235, 0.3); 
                 transition: all 0.3s ease-in-out;
                 -webkit-appearance: none; 
                 -moz-appearance: textfield; 
                 appearance: none; 
        ">
      </div>
    </div>
  `,
      owCancelButton: true,
      confirmButtonText: t('page.marketdetail.purchase.ok'),
      preConfirm: () => {
        const qty = parseInt(document.getElementById('purchaseQty').value, 10);
        if (!qty || qty < 1 || qty > maxQty) {
          Swal.showValidationMessage(
            t('page.marketdetail.purchase.validateQty', { max: maxQty })
          );
        }
        return qty;
      },
    });

    if (!quantity) return;

    try {
      setIsPurchaseInProgress(true);
      const payload = {
        marketNo: assetData.marketNo,
        purchaseCnt: quantity,
      };

      const response = await purchaseAsset(payload);
      Swal.fire({
        title: t('page.marketdetail.purchase.processing'),
        showConfirmButton: false,
        allowOutsideClick: false,
        allowEscapeKey: false,
        didOpen: () => {
          Swal.showLoading();
        },
      });
      setTimeout(() => {
        Swal.close();
        window.location.href = '/mypage/market/purchase';
      }, 12000);
    } catch (error) {
      Swal.fire(t('page.marketdetail.purchase.failTitle'), t('page.marketdetail.purchase.failText'), 'error');
    } finally {
      setIsPurchaseInProgress(false);
    }
  };

  if (!assetData) {
    return (
      <div className="w-full">
        <AssetInfoSectionSkeleton />
      </div>
    );
  }

  return (
    <>
      <div className="flex justify-between w-full items-center border-b-2 py-4 border-gray-200">
        <div className="flex items-center space-x-4">
          <img
            src={
              assetData.creatorProfileImage ||
              'https://www.studiopeople.kr/common/img/default_profile.png'
            }
            alt={t('page.marketdetail.profileAlt')}
            className="w-12 h-12 rounded-full"
          />
          <div>
            <p className="text-lg font-medium">
              {t('page.marketdetail.seller')}: {assetData.saleUserName}
            </p>
          </div>
        </div>
        <p className="text-gray-600">
          {t('page.marketdetail.salePeriod')}: {formatDate(assetData.startDttm)} ~{' '}
          {formatDate(assetData.endDttm)}
        </p>
      </div>

      <div className="flex flex-col space-y-4 mb-8">
        <h2 className="text-2xl font-semibold mt-4">
          {assetData.marketAssetName}
          {assetData?.productNo ? (
            <a
              href={`/goods/detail/${assetData.productNo}`}
              target="_blank"
              rel="noopener noreferrer"
              className="ml-3 text-base text-blue-600 underline align-middle"
            >
              {t('page.marketdetail.viewGoods')}
            </a>
          ) : null}
        </h2>
        <div className="flex justify-between text-gray-600">
          <p>
            {assetData.typeDef} ∙ {timeAgoFromNow(assetData.regDttm)}
          </p>
          <p>
            {t('page.marketdetail.metaverse')}: {' '}
            {assetData.metaverseName === '로블록스'
              ? t('page.marketdetail.none')
              : assetData.metaverseName}
          </p>
        </div>
        <p className="text-xl text-blue-500 font-semibold">
          {t('page.marketdetail.price')}: {assetData.price} ETH
        </p>
        <p className="text-gray-700">
          {t('page.marketdetail.stock')}: {assetData?.inventoryCnt ?? 0}
        </p>
      </div>

      <div className="bg-white p-6 rounded-md border border-gray-300">
        <h3 className="text-xl font-bold mb-4">{t('page.marketdetail.descTitle')}</h3>
        <p
          dangerouslySetInnerHTML={{ __html: assetData.marketAssetDesc }}
          className="text-gray-700 leading-relaxed"
        ></p>
      </div>

      <div className="flex justify-end mt-8">
        <button
          type="button"
          className={`bg-blue-500 text_white w-60 h-14 px-5 rounded-lg justify-center items-center flex ${isPurchaseInProgress ? 'cursor-not-allowed opacity-50' : ''}`}
          disabled={isPurchaseInProgress}
          onClick={handlePurchase}
        >
          <div className="text-white text-lg font-bold">
            {isPurchaseInProgress ? t('page.marketdetail.purchase.buttonProcessing') : t('page.marketdetail.purchase.button')}
          </div>
        </button>
      </div>
    </>
  );
};

export default AssetInfoSection;
