import React, { useEffect, useState } from 'react';
import LeftMenu from '../common/LeftMenu';
import Pagination from '../common/Pagination';
import SearchSection from './SearchSection';
import AssetsTableSection from './AssetsTableSection';
import useAxiosApi from '../../hooks/useAxiosApi';
import { t, getLang, onLangChange } from '../../translation';

const MarketSellAssetsContainer = () => {
  const [searchCondition, setSearchCondition] = useState({
    startDttm: '',
    endDttm: '',
    state: '',
    word: '',
  });

  const [currentSellPage, setCurrentSellPage] = useState(1);
  const [sellData, setSellData] = useState(null);
  const [isSellingLoading, setIsSellingLoading] = useState(false);

  const buildQueryParams = () => {
    const params = new URLSearchParams();
    Object.keys(searchCondition).forEach(key => {
      if (searchCondition[key]) {
        params.append(key, searchCondition[key]);
      }
    });
    params.append('pageNo', currentSellPage);
    return params.toString();
  };

  const queryParams = buildQueryParams();
  const endpoint = `/market/mypage${queryParams ? `?${queryParams}` : ''}`;
  const { execute: fetchMyAssets } = useAxiosApi(endpoint, 'GET');

  useEffect(() => {
    const fetchData = async () => {
      try {
        setIsSellingLoading(true);
        const response = await fetchMyAssets();
        console.log('판매 데이터 응답:', response);
        // 여러 응답 구조를 처리
        setSellData(response);
      } catch (err) {
        console.error('판매 에셋 데이터 가져오기 에러:', err);
        setSellData(null);
      } finally {
        setIsSellingLoading(false);
      }
    };

    fetchData();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [searchCondition, currentSellPage]);

  const handleSearchChange = condition => {
    setSearchCondition(condition);
    setCurrentSellPage(1);
  };

  const [lang, setLangState] = useState(getLang());
  useEffect(() => {
    const off = onLangChange(() => setLangState(getLang()));
    return off;
  }, []);

  return (
    <>
      <main>
        <section className="flex justify-center mt-16">
          <LeftMenu pageName="mypage" />
          <section className="flex-1 ml-4 max-w-screen-lg">
            <div className="flex justify-between">
              <h2 className="text-2xl mb-5">{t('page.mypage.marketSell.title')}</h2>
            </div>

            <SearchSection onSearch={handleSearchChange} />

            <AssetsTableSection
              data={
                sellData?.data?.list ||
                sellData?.list ||
                (Array.isArray(sellData) ? sellData : [])
              }
              isLoading={isSellingLoading}
            />
            <Pagination
              currentPage={currentSellPage}
              totalPage={
                sellData?.data?.totalPage ||
                sellData?.totalPage ||
                sellData?.data?.totalPages ||
                0
              }
              onPageChange={setCurrentSellPage}
            />
          </section>
        </section>
      </main>
    </>
  );
};

export default MarketSellAssetsContainer;
